import React from 'react';
import {Link} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useState, useEffect } from 'react';
import { postNewRecipe } from '../../actions';

export default function Form(){

    const dispatch = useDispatch()
    const [input, serInput] = useState({
        title:'',
        summary:'',
        healthScore:'',
        steps:'',
        diets:[]
    })

    function onHandleSubmit(e){
        e.preventDefault()
        dispatch(postNewRecipe)
        serInput({
            title:'',
        summary:'',
        healthScore:'',
        steps:'',
        diets:[]
        })
    }

    return(
        <div>
            <h3>asd</h3>
            <label value='title'>Titulo</label>
            <input></input>
            <label value='summary'>Resumen</label>
            <input></input>
            <label value='healthScore'>Puntaje</label>
            <input></input>
            <label value='steps'>Paso a paso</label>
            <input></input>
            <label value='diets'>Dietas</label>
            <button onClick={e=>onHandleSubmit(e)}>Crear receta</button>
        </div>
    )
}
