const { POST_INIT_RECIPES, GET_ALL_RECIPES, GET_RECIPE_DETAIL } = require("../actions");


const initialState = {
    recipesOk : [],
    recipes : [],
    recipeDetail: {}
}

function rootReducer (state = initialState, action){
    switch(action.type){
        case POST_INIT_RECIPES:
            return {
                ...state,
                recipesOk: action.payload
            };
        case GET_ALL_RECIPES:
            return{
                ...state,
                recipes: action.payload
            };
        case GET_RECIPE_DETAIL:
            console.log('ENTRA AL REDUCER', action.payload)  
            return{
                ...state,
                recipeDetail: action.payload[0]
            }
    }        
}



module.exports={
    rootReducer
}
