import React from 'react';
import {Link, useHistory} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useState, useEffect } from 'react';
import { postNewRecipe, getAllDiets } from '../../actions';
// import { validate } from './Errors.js';

export default function Form(){

    const dispatch = useDispatch()
    const dietss = useSelector(state=>state?.allDiets)
    const history = useHistory()
    const [errors, seterrors] = useState('')

    useEffect(()=>{
        dispatch(getAllDiets())
    },[dispatch])

    const [input, setInput] = useState({
        title:'',
        summary:'',
        healthScore:'',
        steps:'',
        diets:[],
    })

    function validate(input) {
        let errors = {};
        if (!input.title) {
          errors.title = 'Title is required';
        }
        //  else if (!/\S+@\S+\.\S+/.test(input.title)) {
        //   errors.title = 'Title is invalid';
        // }
        if (!input.summary) {
          errors.summary = 'Summary is required';
        }
        //  else if (!/\d/.test(input.password)) {
        //   errors.summary = 'Summary is invalid';
        // }
        if (!input.healthScore) {
          errors.healthScore = 'HealtScore is required';
        } 
        else if (input.healthScore<0 || input.healthScore>100) {
          errors.healthScore = 'HealtScore is invalid';
        }
        if (!input.steps) {
          errors.steps = 'Steps is required';
        }
        //  else if (!/\S+@\S+\.\S+/.test(input.title)) {
        //   errors.steps = 'Steps is invalid';
        // }
        return errors;
      };

    function handleChange(e){
        e.preventDefault()
        setInput({
            ...input,
            [e.target.name]: e.target.value
        });
        seterrors(validate({
            ...input,
            [e.target.name]:e.target.value
        }))
    }

    function handleSubmit(e){
        e.preventDefault()
        if(errors==={}){
            dispatch(postNewRecipe(input))
            alert('Personaje creado mirey')
            setInput({
                title:'',
                summary:'',
                healthScore:'',
                steps:'',
                diets:[]
                })
            history.push('/home')
        } else{
            console.log(errors)
            alert('completar campos obligatorios')
        }
        
    }

    function handleSelect(e){
        e.preventDefault()
        setInput({
            ...input,
            diets: [...input.diets, e.target.value]
        })
    }

    function handleDelete(el){
        setInput({
            ...input,
            diets: [...input.diets.filter(diet=> diet !== el)]
        })
    }



    return(
        <div>
            <h3>asd</h3>
            <form onSubmit={e=>handleSubmit(e)}>
              <div>
                <label>Titulo</label>
                <input type='text' name='title' value={input.name} onChange={e=>handleChange(e)}></input>
                {errors.title && <span>{errors.title}</span>}
                <label >Resumen</label>
                <input type='text' name='summary' value={input.name} onChange={e=>handleChange(e)}></input>
                {errors.summary && <span>{errors.summary}</span>}
                <label >Puntaje</label>
                <input type='number' name='healthScore' value={input.name} onChange={e=>handleChange(e)}></input>
                {errors.healthScore && <span>{errors.healthScore}</span>}
                <label >Paso a paso</label>
                <input type='text' name='steps' value={input.name} onChange={e=>handleChange(e)}></input>
                {errors.steps && <span>{errors.steps}</span>}
                <label >Dietas</label>
                
                
              </div>
            <div>
            <select onChange={e=>handleSelect(e)}>
                <option>Tipos de dieta</option>
                    {
                        dietss.map(e=>(
                        <option key={e.id} value={input.diets.name}>{e.name}</option>)
                        )
                    }
                    
              </select>
              <ul><li>Dietas seleccionadas: {input.diets.map(el=>el + ' ,')}</li></ul>
            </div>
              
                
                <input type='submit'></input>  
            </form>
            {input.diets.map(el=>(
                <div>
                    <p>{el}</p>
                    <button onClick={e=>handleDelete(el)}>x</button>
                </div>

            ))}
            
        </div>
    )
}
