import React from 'react';
import {Link} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useState, useEffect } from 'react';
import { postNewRecipe, getAllDiets } from '../../actions';

export default function Form(){

    const dispatch = useDispatch()
    const dietss = useSelector(state=>state?.allDiets)
    const [input, setInput] = useState({
        title:'',
        summary:'',
        healthScore:'',
        steps:'',
        diets:[]
    })

    useEffect(()=>{
        dispatch(getAllDiets())
    },[dispatch, dietss])

    function handleChange(e){
        e.preventDefault()
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }

    function handleSubmit(e){
        e.preventDefault()
        dispatch(postNewRecipe(input))
        setInput({
        title:'',
        summary:'',
        healthScore:'',
        steps:'',
        diets:[]
        })
    }

    return(
        <div>
            <h3>asd</h3>
            <form>
              <div>
                <label>Titulo</label>
                <input type='text' name='title' value={input.name} onChange={e=>handleChange(e)}></input>
                <label >Resumen</label>
                <input type='text' name='summary' value={input.name} onChange={e=>handleChange(e)}></input>
                <label >Puntaje</label>
                <input type='number' name='healthScore' value={input.name} onChange={e=>handleChange(e)}></input>
                <label >Paso a paso</label>
                <input type='text' name='steps' value={input.name} onChange={e=>handleChange(e)}></input>
                <label >Dietas</label>
                {console.log('SOY DIETA', dietss)}
                
              </div>  
              <select>
                    {dietss.map(e=>{
                        <option value={e.name}>{e.name}</option>
                    })}
                </select>
                <button onClick={e=>handleSubmit(e)}>Crear receta</button>  
            </form>
            
        </div>
    )
}
