import React, { useEffect } from "react";
import s from './Paginado.module.css';
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { maxPageNumber, minPageNumber, savePage } from "../../actions";



export default function Paginado ({recipesPerPage, allRecipes}){
    const pageNumber = []
    const actualP = useSelector(state=>state?.recordedPage)
    // console.log('PAGINA ACTUAL',actualP)
    // console.log('PAGE NUMBER', pageNumber)
    const dispatch = useDispatch()
    const maxPage = useSelector(state=>state?.maxPageNumber)
    const minPage = useSelector(state=>state?.minPageNumber)
    
    for(let i=actualP; i<=Math.ceil( allRecipes / recipesPerPage ); i++){
        pageNumber.push(i)
    }
    // const pagge = pageNumber.slice(actualP-1)
    // useEffect(()=>{
    //     console.log('MAXPAGENUMBER1',maxPageNumberLimit+1);
    // },[])
    
    const pageNumberLimit= 3; //total de paginas que quiero renderizar
    // const [maxPageNumberLimit, setmaxPageNumberLimit]= useState(4);
    // const [minPageNumberLimit, setminPageNumberLimit]= useState(0);
    // const renderPageNumbers= pageNumber.map((number)=>{
        
    //     if(number < maxPageNumberLimit+1 && number > minPageNumberLimit){
    //         return(
    //             <li 
    //             key={number} 
    //             id={number} 
    //             onClick={paginado}
    //             className={currentPage === number? "active" : null} >
    //                 {number}
    //             </li>
    //         )
    //     }else{
    //         return null;
    //     }
    // }) 
    // const paginateMas = () => setCurrentPage(currentPage + 1)
    // const paginateMenos = () => setCurrentPage(currentPage - 1)
    // function handlePrevBut() {
    //     setCurrentPage(currentPage-1)
    //     setminPageNumberLimit(minPageNumberLimit-1)
    //     setmaxPageNumberLimit(maxPageNumberLimit-1)

    // }

    // function handleNextBut() {
    //     setCurrentPage(currentPage+1)
    //     setmaxPageNumberLimit(maxPageNumberLimit+1)
    //     setminPageNumberLimit(minPageNumberLimit+1)

    const paginado = (number) =>{
        
        // setmaxPageNumberLimit(maxPageNumberLimit+pageNumber-actualPage)
        // setminPageNumberLimit(minPageNumberLimit+pageNumber-actualPage)
        dispatch(savePage(number))
    }
    // for (let i = actualP; i < pageNumber.length; i++) {
                //     if(i < maxPageNumberLimit+1 && i > minPageNumberLimit){
                //     return (
                //         <li 
                //     key={i} 
                //     id={i} 
                //     onClick={()=>paginado(i)}
                //     className={currentPage === i? s.active : s.pages}
                //      >
                //         {i}
                //     </li>
                //     )}
                // }
    
    function handlePrevBut() {
                    // setCurrentPage(currentPage-1)
                    // setminPageNumberLimit(minPageNumberLimit-1)
                    // setmaxPageNumberLimit(maxPageNumberLimit-1)
        // console.log ('CURRENT PAGE1', currentPage-1)
        dispatch(savePage(actualP-1))
        minPageNumber(-1)
        maxPageNumber(-1)
        }
            
    function handleNextBut() {
            // setCurrentPage(currentPage+1)
                    // setmaxPageNumberLimit(maxPageNumberLimit+1)
                    // setminPageNumberLimit(minPageNumberLimit+1)
                    // console.log ('CURRENT PAGE2', currentPage+1)
        dispatch(savePage(actualP+1))
        // console.log('MAXPAGENUMBER1',maxPageNumberLimit+1)
        minPageNumber(1)
        maxPageNumber(1)
            console.log('PAGENUMBER',pageNumber)
            console.log('MAXPAGE', maxPage)
        }
       
    // const pagg = {
        
    // }
    // console.log('SLICE', pageNumber.slice(actualP))

    // }
    
    return (
        <nav >
            
            <ul className={s.pageNumbers}>
                <li className={actualP>1?s.prevNext:s.noPrevBut} onClick={handlePrevBut}>
                    {/* <button className={currentPage>1?s.prevBut:s.noPrevBut} >Prev</button> */}
                    Prev
                </li>
                {/* {console.log('MAXNUMBER', maxPageNumberLimit)} */}
                
                {
                    pageNumber.map((number)=>{  //.slice(actualP-1)
                        if(number < maxPage+1 && number > minPage){
                            // console.log('NUMERO', number)
                            return(
                                <li 
                                key={number} 
                                id={number} 
                                onClick={()=>paginado(number)}
                                className={actualP === number? s.active : s.pages}
                                >
                                {number}
                                </li>
                                )}
                        }) 
                            
                }
            
                       
                {/* {pageNumber && pageNumber.map(num=>(
                    <li key={num} id={num}>
                       <a onClick={()=>paginado(num)}>{num}</a> 
                    </li>
                ))} */}
                <li className={actualP!==pageNumber.length?s.prevNext:s.noPrevBut} onClick={handleNextBut}>
                    {/* <button className={currentPage!==pageNumber.length?s.prevBut:s.noPrevBut} >Next</button> */}
                    Next
                </li>
            
            
            </ul>
            
        </nav>
    )
}
