import React from 'react';
import {Link, useParams} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useState, useEffect } from 'react';
import { getRecipeDetail, deleteDetail } from '../../actions';
import s from './Detail.module.css';

// Ruta de detalle de receta: debe contener

// [ ] Los campos mostrados en la ruta principal para cada receta (imagen, nombre, tipo de plato y tipo de dieta)
// [ ] Resumen del plato
// [ ] Nivel de "comida saludable" (health score)
// [ ] Paso a paso

export default function RecipDetail(props){

    const dispatch = useDispatch()
    let {id} = useParams()
    // let id = props.params.id
    let recipeDetail = useSelector (state => state?.recipeDetail)
    

    useEffect(()=>{
        dispatch(getRecipeDetail(id));
        return function(){
            // console.log('ENTRA AL WILLUNMO')
            dispatch(deleteDetail())
        }
    },[dispatch,id])

    return (
        <div className={s.container}>
            {/* {console.log('SOY id',id)}
            {console.log('ESTADO',recipeDetail)} */}
            <div className={s.img}>
                <img src={recipeDetail.image} alt='not found'/>
            </div>
            <div className={s.text}>
            <h1 className={s.titulo}>{recipeDetail.title}</h1>
            <div className={s.data}>
                <div className={s.score}>
                <h3>HealthScore: {recipeDetail.healthScore}</h3>
                </div>
            {
            recipeDetail.dishTypes && recipeDetail.dishTypes.length>0?
            <div>
            <h3>DishType:</h3>
            {recipeDetail.dishTypes.map(d=>{
                return <li>{d.name}</li>
            })}
            </div>
            :<li>There is no dishType associated yet</li>
            }
            {
            recipeDetail.diets && recipeDetail.diets.length>0?
            <div>
            <h3>Diets:</h3>
            {recipeDetail.diets.map(d=>{
                return <li>{d.name}</li>
            })}
            </div>
            :<li>There is no diet associated yet</li>
            }
            <h4>Summary: {recipeDetail.summary?.replace(/<[^>]+>/g, '')}</h4>
            <div className={s.steps}>
            {
            recipeDetail.steps? <h4>Steps: {recipeDetail.steps}</h4> : <h4>There is no steps associated yet</h4> 
            }
            </div>
            
            </div>
            
            </div>
            
            
        </div>
    )
}
