import React, { useEffect, useState } from 'react';
import {Link} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getAllRecipes, postInitRecipes } from '../../actions';
import Card from '../Card/Cards.js';
import Paginado from '../Paginado/Paginado';

// Ruta principal: debe contener

// [ ] Input de búsqueda para encontrar recetas por nombre
// [ ] Área donde se verá el listado de recetas. Deberá mostrar su:
// Imagen
// Nombre
// Tipo de dieta (vegetariano, vegano, apto celíaco, etc)
// [ ] Botones/Opciones para filtrar por por tipo de dieta
// [ ] Botones/Opciones para ordenar tanto ascendentemente como descendentemente las recetas por orden alfabético y por health score (nivel de comida saludable).
// [ ] Paginado para ir buscando y mostrando las siguientes recetas, 9 recetas por pagina, mostrando las primeros 9 en la primer pagina.

export default function Home(){

    const dispatch = useDispatch()
    
    const allRecipes = useSelector (state=>state?.recipes)

    useEffect(()=>{
        dispatch(getAllRecipes());
    },[dispatch])

    const handleRecharge = (e) => {
        dispatch(getAllRecipes()) 
    }
    
    const [currentPage, setCurrentPage] = useState(1)
    const [recipesPerPage, setRecipesPerPage] = useState(9)
    const indexOfLastRecipe = currentPage * recipesPerPage
    const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage
    const currentRecipes = allRecipes.slice(indexOfFirstRecipe, indexOfLastRecipe)

    const paginado = (pageNumber) =>{
        setCurrentPage(pageNumber)
    }

    return (
        <div>
            <h1>Home</h1>
            {/* <div>
                <label>Receta:</label>
                <input type="text" id="title" />
                <Link to = '/detail'>
                <button>Buscar</button>
                </Link>
            </div> */}
            <Link to = '/detail'>
                Ingresar
            </Link>
            <button  onClick={(e)=> handleRecharge()}>Recargar</button>
            <div>
                <select>
                    <option value='asc'>(a-z) Ascendent</option>
                    <option value='desc'>(z-a) Descendent</option>
                </select>
                <select>
                    <option value='low'>Lower</option>
                    <option value='hi'>Higher</option>
                </select>
                <select>
                    <option value='all'>Todos</option>
                    <option value='glu'>gluten free</option>
                    <option value='dai'>dairy free</option>
                    <option value='lac'>lacto ovo vegetarian</option>
                    <option value='veg'>vegan</option>
                    <option value='pal'>paleolithic</option>
                    <option value='pri'>primal</option>
                    <option value='who'>whole 30</option>
                    <option value='pes'>pescatarian</option>
                    <option value='ket'>ketogenic</option>
                    <option value='fod'>fodmap friendly</option>
                </select>
            {/* <Paginado
            recipesPerPage = {recipesPerPage}
            allRecipes = {allRecipes.length}
            paginado = {paginado}/> */}
            {   
                allRecipes && allRecipes.map(el=>{
                    return(
                        <div>
                            {/* {console.log('ELEMENTO', el)} */}
                            <Link key={el.id} to={`/detail/${el.id}`}>
                                <Card title={el.title} image={el.image} diets={el.diets} key={el.id}/>
                            </Link>
                            
                        </div>

                    
                    )
                })
            }
            </div>
        </div>
    )
}
