import axios from 'axios';
export const POST_INIT_RECIPES = 'POST_INIT_RECIPES';

//aca armo la ruta

export function postInitRecipes (recipes) {
    return function(dispatch) {
      return axios.get(`http://localhost:3001/`)
        .then(json => {
          dispatch({ 
            type: POST_ALL_RECIPES, 
            payload: json,
        });
        });
    };
  };
